import React, { useState } from 'react';

import './ExpenseForm.css';

const ExpenseForm = (props) => {
    const [enteredTitle,setEnteredTitle] = useState('');
    const [enteredAmount,setEnteredAmout] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    };
    const amountChangeHandler = (event) => { 
        setEnteredAmout(event.target.value);
    };
    const dateChangeHandler = (event) => { 
        setEnteredDate(event.target.value);
    };

    const submitHandler = (event) => {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate),
            id: Math.random().toFixed(7),
        };
        props.onSubmitHandler(expenseData);

        setEnteredTitle('');
        setEnteredAmout('');
        setEnteredDate('');

    }

    const cancelHandler = (event) => {
        event.preventDefault();

    }






  return (
    <form onSubmit={submitHandler} onCancel={cancelHandler}> 
      <div className='new-expense__controls'>
        <div className='new-expense__control'>
          <label>Title</label>
          <input type='text'  value={enteredTitle} onChange={titleChangeHandler}/>
        </div>
        <div className='new-expense__control'>
          <label>Amount</label>
          <input type='number' min='0.01' step='0.01' value={enteredAmount} onChange={amountChangeHandler}/>
        </div>
        <div className='new-expense__control'>
          <label>Date</label>
          <input type='date' min='2019-01-01' max='2024-12-31' value={enteredDate} onChange={dateChangeHandler}/>
        </div>
      </div>
          <div className='new-expense__actions'>
        <button type='cancel'>Cancel</button>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
