import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "./UI/Card";
import ExpenseFilter from "./ExpenseFilter";

const Expenses = (props) => {
  const [chosenYear, setChosenYear] = useState("2022");

  const filterChangeHandler = (enteredYear) => {
    setChosenYear(enteredYear);
  };

  // const filteredExpenses = props.items.filter(expense => { return expense.date.getFullYear().toString() === chosenYear });

  return (
    <Card className="expenses">
      <ExpenseFilter
        selectedYear={chosenYear}
        onChangeFilter={filterChangeHandler}
      />
      {props.items
        .filter((expense) => {
          return expense.date.getFullYear().toString() === chosenYear;
        })
        .map((expenses) => (
          <ExpenseItem
            title={expenses.title}
            amount={expenses.amount}
            date={expenses.date}
            key={expenses.id}
          />
        ))}
      ;
      {/* {props.items.map((expenses) => <ExpenseItem
        title={expenses.title}
        amount={expenses.amount}
        date={expenses.date} />).filter((expenses) =>  expenses.date.getFullYear() === setChosenYear)} */}
    </Card>
  );
};

export default Expenses;
